interface IAccountManager {
    enum Symbols {
        ETH_USD_LONG,
        ETH_USD_SHORT
    }
    function keyData(uint256 id) external returns (
        Symbols symbol, 
        address doppelgangerContract, 
        bool isLong,
        address indexToken,
        address user
    );
    function indexTokenBySymbol(Symbols symbol) external returns (address indexToken);
    function getPositionDelta(uint256 id) external view returns (bool isProfit, uint256 profit);
    function getPosition(uint256 id) external view returns (
        uint256 size, 
        uint256 collateral, 
        uint256 averagePrice, 
        uint256 entryFundingRate, 
        uint256 reserveAmount, 
        uint256 realisedPnl,
        bool isProfit, 
        uint256 lastIncreasedTime
    );
    function currentPrice(uint256 id) external view returns (uint256 price);
    function isLong(uint256 id) external view returns (bool);
}","keccak256":"0xf1ef5609b4aa86f0ddaa9da9d68dc08862c19e1c200a976e306d09dbeca52a8e"},"contracts/interfaces/IAccountManagerToken.sol":{"content":"pragma solidity 0.8.6;

interface IAccountManagerToken {
    function mint(address to, uint256 id) external;
    function addTokenId(uint256 value) external;
    function tokenId() external returns (uint256);
}","keccak256":"0x09315b038d6ecc380c256ce8d241e367882cdeb86ea48fb3eafcc451eb926d3b"},"contracts/interfaces/IOrderBook.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.6 || 0.6.12;

interface IOrderBook {
	function getSwapOrder(address _account, uint256 _orderIndex) external view returns (
        address path0, 
        address path1,
        address path2,
        uint256 amountIn,
        uint256 minOut,
        uint256 triggerRatio,
        bool triggerAboveThreshold,
        bool shouldUnwrap,
        uint256 executionFee
    );

    function getIncreaseOrder(address _account, uint256 _orderIndex) external view returns (
        address purchaseToken, 
        uint256 purchaseTokenAmount,
        address collateralToken,
        address indexToken,
        uint256 sizeDelta,
        bool isLong,
        uint256 triggerPrice,
        bool triggerAboveThreshold,
        uint256 executionFee
    );

    function getDecreaseOrder(address _account, uint256 _orderIndex) external view returns (
        address collateralToken,
        uint256 collateralDelta,
        address indexToken,
        uint256 sizeDelta,
        bool isLong,
        uint256 triggerPrice,
        bool triggerAboveThreshold,
        uint256 executionFee
    );

    function createIncreaseOrder(
        address[] memory _path,
        uint256 _amountIn,
        address _indexToken,
        uint256 _minOut,
        uint256 _sizeDelta,
        address _collateralToken,
        bool _isLong,
        uint256 _triggerPrice,
        bool _triggerAboveThreshold,
        uint256 _executionFee,
        bool _shouldWrap
    ) external payable;
    function cancelIncreaseOrder(uint256 _orderIndex) external;
    function executeIncreaseOrder(address _address, uint256 _orderIndex, address payable _feeReceiver) external;
    function updateIncreaseOrder(uint256 _orderIndex, uint256 _sizeDelta, uint256 _triggerPrice, bool _triggerAboveThreshold) external;

    function createDecreaseOrder(
        address _indexToken,
        uint256 _sizeDelta,
        address _collateralToken,
        uint256 _collateralDelta,
        bool _isLong,
        uint256 _triggerPrice,
        bool _triggerAboveThreshold
    ) external payable;
    function executeDecreaseOrder(address _address, uint256 _orderIndex, address payable _feeReceiver) external;
    function cancelDecreaseOrder(uint256 _orderIndex) external;
    function updateDecreaseOrder(
        uint256 _orderIndex,
        uint256 _collateralDelta,
        uint256 _sizeDelta,
        uint256 _triggerPrice,
        bool _triggerAboveThreshold
    ) external;

    function minExecutionFee() external view returns (uint256 minExecutionFee);
}
