contracts/AccountManager.sol.content:
pragma solidity 0.8.6;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./interfaces/IDDL_GMX.sol";
import "./interfaces/IOrderBook.sol";
import "./Doppelganger.sol";
import "hardhat/console.sol";

contract AccountManager is
    ERC721("GMX position tokenization", "GMXPT"),
    Ownable
{
    IDDL_GMX public ddl_gmx;
    IRouter public immutable router;
    IOrderBook public immutable orderBook;
    uint256 public tokenId = 0;
    mapping(address => address) public doppelgangerMap;
    struct KeyInformation {
        string symbol;
        address doppelgangerContract;
        bool isLong;
        address indexToken;
        address user;
    }
    mapping(uint256 => KeyInformation) public keyData;
    mapping(address => string) public symbolByIndexToken;
    mapping(string => address) public indexTokenBySymbol;
    mapping(address => bool) public permissions;
    string[8] arrSymbols = [
        "ETH_USD_LONG",
        "BTC_USD_LONG",
        "LINK_USD_LONG",
        "UNI_USD_LONG",
        "ETH_USD_SHORT",
        "BTC_USD_SHORT",
        "LINK_USD_SHORT",
        "UNI_USD_SHORT"
    ];

    address[8] indexTokenArr;

    constructor(
        IRouter _router, 
        IOrderBook _orderBook,
        address[8] memory _indexTokenArr,
        bool[8] memory _permissions
    ) {
        router = _router;
        orderBook = _orderBook;
        indexTokenArr = _indexTokenArr;
        for (uint256 i = 0; i < 8; i++) {
            symbolByIndexToken[_indexTokenArr[i]] = arrSymbols[i];
            indexTokenBySymbol[arrSymbols[i]] = _indexTokenArr[i];
            permissions[_indexTokenArr[i]] = _permissions[i]; 
        }
    }

    /**
     * @notice set new DDL_GMX address
     * @param value the address of DDL_GMX
     **/
    function setDDL_GMX(IDDL_GMX value) external onlyOwner {
        ddl_gmx = value;
    }

    /**
     * @notice set permissions for trading pairs
     * @param symbol trough name
     * @param value true or false 
     **/
     function setPermission(string memory symbol, bool value) external onlyOwner {
        permissions[indexTokenBySymbol[symbol]] = value;
    }

    /**
     * @notice creates DoppelGanger account for the user
     **/
    function createDoppelgangerGMX() public {
        require(
            doppelgangerMap[msg.sender] == address(0),
            "DoppelGanger for this address already exist"
        );
        uint256 id = tokenId;
        tokenId += 8;
        uint256[8] memory keys;
        for (uint256 key = 0; key < 8; key++) {
            keys[key] = (id + key);
        }
        Doppelganger newContract = new Doppelganger(
            ddl_gmx.positionRouter(),
            router,
            orderBook,
            ddl_gmx.USDC(),
            keys,
            indexTokenArr,
            address(this),
            address(ddl_gmx)
        );
        doppelgangerMap[msg.sender] = address(newContract);
        for (uint256 i = 0; i < 8; i++) {
            keyData[id + i] = KeyInformation(
                arrSymbols[i],
                address(newContract),
                i < 4 ? true : false,
                indexTokenArr[i],
                msg.sender
            );
            _safeMint(msg.sender, id + i);
        }
    }

    /**
     * @notice returns getPositionDelta from GMX by the collateral ID
     * @param id collateral ID
     **/
    function getPositionDelta(uint256 id)
        public
        view
        returns (bool isProfit, uint256 profit)
    {
        return
            ddl_gmx.vault().getPositionDelta(
                keyData[id].doppelgangerContract,
                keyData[id].isLong
                    ? keyData[id].indexToken
                    : address(ddl_gmx.USDC()),
                keyData[id].indexToken,
                keyData[id].isLong
            );
    }

    /**
     * @notice returns getPosition by the collateral
     * @param id collateral ID
     **/
    function getPosition(uint256 id)
        public
        view
        returns (
            uint256 size,
            uint256 collateral,
            uint256 averagePrice,
            uint256 entryFundingRate,
            uint256 reserveAmount,
            uint256 realisedPnl,
            bool isProfit,
            uint256 lastIncreasedTime
        )
    {
        (
            size,
            collateral,
            averagePrice,
            entryFundingRate,
            reserveAmount,
            realisedPnl,
            isProfit,
            lastIncreasedTime
        ) = ddl_gmx.vault().getPosition(
            keyData[id].doppelgangerContract,
            keyData[id].isLong
                ? keyData[id].indexToken
                : address(ddl_gmx.USDC()),
            keyData[id].indexToken,
            keyData[id].isLong
        );
    }

    /**
     * @param id position ID
     * @notice return currentPrice for the asset
     **/
    function currentPrice(uint256 id) public view returns (uint256) {
        KeyInformation memory data = keyData[id];
        if (ddl_gmx.isLong(id)) {
            return ddl_gmx.vault().getMaxPrice(data.indexToken);
        }
        return ddl_gmx.vault().getMinPrice(data.indexToken);
    }

    /**
     * @notice used to increase position on GMX
     **/
    function createIncreasePosition(
        address[] memory _path,
        address _indexToken,
        uint256 _amountIn,
        uint256 _minOut,
        uint256 _sizeDelta,
        bool _isLong,
        uint256 _acceptablePrice,
        bytes32 _referralCode
    ) external payable {
        require(permissions[_indexToken], "trading on this pair is stopped");
        (_isLong ? ERC20(_indexToken) : ddl_gmx.USDC()).transferFrom(
            msg.sender,
            doppelgangerMap[msg.sender],
            _amountIn
        );
        uint256 executionFee = ddl_gmx.positionRouter().minExecutionFee();
        Doppelganger(doppelgangerMap[msg.sender]).deposit{
            value: executionFee
        }();
        Doppelganger(doppelgangerMap[msg.sender]).createIncreasePosition(
            msg.sender,
            _path,
            _indexToken,
            _amountIn,
            _minOut,
            _sizeDelta,
            _isLong,
            _acceptablePrice,
            executionFee,
            _referralCode
        );
    }

    /**
     * @notice used to increase position on GMX (only ETH)
     **/
    function createIncreasePositionETH(
        address[] memory _path,
        address _indexToken,
        uint256 _amountIn,
        uint256 _minOut,
        uint256 _sizeDelta,
        bool _isLong,
        uint256 _acceptablePrice,
        uint256 _executionFee,
        bytes32 _referralCode
    ) external payable {
        require(permissions[_indexToken], "trading on this pair is stopped");
        uint256 executionFee = ddl_gmx.positionRouter().minExecutionFee();
        Doppelganger(doppelgangerMap[msg.sender]).deposit{
            value: executionFee + _amountIn
        }();
        Doppelganger(doppelgangerMap[msg.sender]).createIncreasePositionETH(
            msg.sender,
            _path,
            _indexToken,
            _amountIn,
            _minOut,
            _sizeDelta,
            _isLong,
            _acceptablePrice,
            _executionFee,
            _referralCode
        );
    }

    /**
     * @notice used to decrease position on GMX
     **/
    function createDecreasePosition(
        address[] memory _path,
        address _indexToken,
        uint256 _collateralDelta,
        uint256 _sizeDelta,
        bool _isLong,
        uint256 _acceptablePrice,
        uint256 _minOut,
        uint256 _executionFee,
        bool _withdrawETH
    ) external payable {
        require(permissions[_indexToken], "trading on this pair is stopped");
        uint256 executionFee = ddl_gmx.positionRouter().minExecutionFee();
        Doppelganger(doppelgangerMap[msg.sender]).deposit{
            value: executionFee
        }();
        Doppelganger(doppelgangerMap[msg.sender]).createDecreasePosition(
            msg.sender,
            _path,
            _indexToken,
            _collateralDelta,
            _sizeDelta,
            _isLong,
            _acceptablePrice,
            _minOut,
            _executionFee,
            _withdrawETH
        );
    }

    function createIncreaseOrder(
        address[] memory _path,
        uint256 _amountIn,
        address _indexToken,
        uint256 _minOut,
        uint256 _sizeDelta,
        address _collateralToken,
        bool _isLong,
        uint256 _triggerPrice,
        bool _triggerAboveThreshold,
        uint256 _executionFee,
        bool _shouldWrap
    ) external payable {
        require(permissions[_indexToken], "trading on this pair is stopped");
        ddl_gmx.USDC().transferFrom(
            msg.sender,
            doppelgangerMap[msg.sender],
            _amountIn
        );
        uint256 executionFee = ddl_gmx.positionRouter().minExecutionFee();
        Doppelganger(doppelgangerMap[msg.sender]).deposit{
            value: executionFee
        }();
        Doppelganger(doppelgangerMap[msg.sender]).createIncreaseOrder(
            msg.sender,
            _path,
            _amountIn,
            _indexToken,
            _minOut,
            _sizeDelta,
            _collateralToken,
            _isLong,
            _triggerPrice,
            _triggerAboveThreshold,
            _executionFee,
            _shouldWrap
        );
    }
}

contracts/interfaces/IDDL_GMX.sol.content:
pragma solidity 0.8.6;

import "./IVault.sol";
import "./IPositionRouter.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IDDL_GMX {
    function vault() external view returns (IVault vault);
    function positionRouter() external view returns (IPositionRouter positionRouter);
    function isLong(uint256 id) external view returns (bool);
    function USDC() external view returns(IERC20 USDC);
}

