// SPDX-License-Identifier: MIT

pragma solidity 0.8.6 || 0.6.12;

interface IOrderBook {
	function getSwapOrder(address _account, uint256 _orderIndex) external view returns (
        address path0, 
        address path1,
        address path2,
        uint256 amountIn,
        uint256 minOut,
        uint256 triggerRatio,
        bool triggerAboveThreshold,
        bool shouldUnwrap,
        uint256 executionFee
    );

    function getIncreaseOrder(address _account, uint256 _orderIndex) external view returns (
        address purchaseToken, 
        uint256 purchaseTokenAmount,
        address collateralToken,
        address indexToken,
        uint256 sizeDelta,
        bool isLong,
        uint256 triggerPrice,
        bool triggerAboveThreshold,
        uint256 executionFee
    );

    function getDecreaseOrder(address _account, uint256 _orderIndex) external view returns (
        address collateralToken,
        uint256 collateralDelta,
        address indexToken,
        uint256 sizeDelta,
        bool isLong,
        uint256 triggerPrice,
        bool triggerAboveThreshold,
        uint256 executionFee
    );

    function createIncreaseOrder(
        address[] memory _path,
        uint256 _amountIn,
        address _indexToken,
        uint256 _minOut,
        uint256 _sizeDelta,
        address _collateralToken,
        bool _isLong,
        uint256 _triggerPrice,
        bool _triggerAboveThreshold,
        uint256 _executionFee,
        bool _shouldWrap
    ) external payable;
    function cancelIncreaseOrder(uint256 _orderIndex) external;
    function executeIncreaseOrder(address _address, uint256 _orderIndex, address payable _feeReceiver) external;
    function updateIncreaseOrder(uint256 _orderIndex, uint256 _sizeDelta, uint256 _triggerPrice, bool _triggerAboveThreshold) external;

    function createDecreaseOrder(
        address _indexToken,
        uint256 _sizeDelta,
        address _collateralToken,
        uint256 _collateralDelta,
        bool _isLong,
        uint256 _triggerPrice,
        bool _triggerAboveThreshold
    ) external payable;
    function executeDecreaseOrder(address _address, uint256 _orderIndex, address payable _feeReceiver) external;
    function cancelDecreaseOrder(uint256 _orderIndex) external;
    function updateDecreaseOrder(
        uint256 _orderIndex,
        uint256 _collateralDelta,
        uint256 _sizeDelta,
        uint256 _triggerPrice,
        bool _triggerAboveThreshold
    ) external;
}

contracts/Doppelganger.sol.content:
pragma solidity 0.8.6||0.6.12;

import "./interfaces/IPositionRouter.sol";
import "./interfaces/IRouter.sol";
import "./interfaces/IOrderBook.sol";
import "./interfaces/IAccountManager.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "hardhat/console.sol";

contract Doppelganger {
    using SafeERC20 for IERC20;

    IPositionRouter public positionRouter;
    IOrderBook public orderBook;
    mapping(address => mapping(bool => uint256)) public keyByIndexToken;
    ERC721 public GMXPT;
    IRouter public immutable router;
    IERC20 public USDC;
    address[4] public indexTokenArray;
    address public accountManager;
    address public ddl_gmx;

    constructor(
        IPositionRouter _positionRouter,
        IRouter _router,
        IOrderBook _orderBook,
        IERC20 _USDC,
        uint256[8] memory keyArray,
        address[8] memory _indexTokenArray,
        address _accountManager,
        address _ddl_gmx
    ) {
        USDC = _USDC;
        positionRouter = _positionRouter;
        router = _router;
        orderBook = _orderBook;
        for (uint256 i = 0; i < 8; i++) {
            if (i < 4) {
                indexTokenArray[i] = _indexTokenArray[i];
            }
            keyByIndexToken[_indexTokenArray[i]][
                i < 4 ? true : false
            ] = keyArray[i];
        }
        GMXPT = ERC721(_accountManager);
        accountManager = _accountManager;
        ddl_gmx = _ddl_gmx;
    }

    /**
     * @notice TODO
     **/
    function deposit() public payable {}

    function _checkKeyAndMsgSender(address user, address _indexToken, bool _isLong) private {
        require(
            GMXPT.ownerOf(keyByIndexToken[_indexToken][_isLong]) == user,
            "you don't have a key"
        );
        if (msg.sender != ddl_gmx) {
            require(msg.sender == accountManager, "invalid msg.sender");
        }
    }

    function createIncreasePosition(
        address user,
        address[] memory _path,
        address _indexToken,
        uint256 _amountIn,
        uint256 _minOut,
        uint256 _sizeDelta,
        bool _isLong,
        uint256 _acceptablePrice,
        uint256 _executionFee,
        bytes32 _referralCode
    ) external payable {
        _checkKeyAndMsgSender(user, _indexToken, _isLong);
        positionRouter.createIncreasePosition{value: _executionFee}(
            _path,
            _indexToken,
            _amountIn,
            _minOut,
            _sizeDelta,
            _isLong,
            _acceptablePrice,
            _executionFee,
            _referralCode
        );
    }

    function createIncreasePositionETH(
        address user,
        address[] memory _path,
        address _indexToken,
        uint256 _amountIn,
        uint256 _minOut,
        uint256 _sizeDelta,
        bool _isLong,
        uint256 _acceptablePrice,
        uint256 _executionFee,
        bytes32 _referralCode
    ) external payable {
        _checkKeyAndMsgSender(user, _indexToken, _isLong);
        positionRouter.createIncreasePositionETH{value: _executionFee + _amountIn}(
            _path,
            _indexToken,
            _minOut,
            _sizeDelta,
            _isLong,
            _acceptablePrice,
            _executionFee,
            _referralCode
        );
    }

    function createDecreasePosition(
        address user,
        address[] memory _path,
        address _indexToken,
        uint256 _collateralDelta,
        uint256 _sizeDelta,
        bool _isLong,
        uint256 _acceptablePrice,
        uint256 _minOut,
        uint256 _executionFee,
        bool _withdrawETH
    ) external payable {
        address keyOwner = GMXPT.ownerOf(keyByIndexToken[_indexToken][_isLong]);
        require(keyOwner == user, "you don't have a key");
        if (msg.sender != ddl_gmx) {
            require(msg.sender == accountManager, "invalid msg.sender");
        }
        positionRouter.createDecreasePosition{value: _executionFee}(
            _path,
            _indexToken,
            _collateralDelta,
            _sizeDelta,
            _isLong,
            keyOwner,
            _acceptablePrice,
            _minOut,
            _executionFee,
            _withdrawETH
        );
    }

    function createIncreaseOrder(
        address user,
        address[] memory _path,
        uint256 _amountIn,
        address _indexToken,
        uint256 _minOut,
        uint256 _sizeDelta,
        address _collateralToken,
        bool _isLong,
        uint256 _triggerPrice,
        bool _triggerAboveThreshold,
        uint256 _executionFee,
        bool _shouldWrap
    ) external payable {
        _checkKeyAndMsgSender(user, _indexToken, _isLong);
        orderBook.createIncreaseOrder{value: _executionFee}(
            _path,
            _amountIn,
            _indexToken,
            _minOut,
            _sizeDelta,
            _collateralToken,
            _isLong,
            _triggerPrice,
            _triggerAboveThreshold,
            _executionFee,
            _shouldWrap
        );
    }

    // function cancelIncreaseOrder(address user, uint256 _orderIndex) external {
    //     _checkKeyAndMsgSender(user, _indexToken, _isLong);
    //     orderBook.cancelIncreaseOrder(_orderIndex);
    // }

    // function updateIncreaseOrder(uint256 _orderIndex, uint256 _sizeDelta, uint256 _triggerPrice, bool _triggerAboveThreshold) external {
    //     _checkKeyAndMsgSender(user, _indexToken, _isLong);
    //     orderBook.updateIncreaseOrder(_orderIndex, _sizeDelta, _triggerPrice, _triggerAboveThreshold);
    // }

    // function createDecreaseOrder(
    //     address _indexToken,
    //     uint256 _sizeDelta,
    //     address _collateralToken,
    //     uint256 _collateralDelta,
    //     bool _isLong,
    //     uint256 _triggerPrice,
    //     bool _triggerAboveThreshold
    // ) external payable {
    //     _checkKeyAndMsgSender(user, _indexToken, _isLong);
    //     orderBook.createDecreaseOrder(
    //         _indexToken,
    //         _sizeDelta,
    //         _collateralToken,
    //         _collateralDelta,
    //         _isLong,
    //         _triggerPrice,
    //         _triggerAboveThreshold
    //     );
    // }

    // function cancelDecreaseOrder(uint256 _orderIndex) public {
    //     _checkKeyAndMsgSender(user, _indexToken, _isLong);
    //     orderBook.cancelDecreaseOrder(_orderIndex);
    // }

    // function updateDecreaseOrder(
    //     uint256 _orderIndex,
    //     uint256 _collateralDelta,
    //     uint256 _sizeDelta,
    //     uint256 _triggerPrice,
    //     bool _triggerAboveThreshold
    // ) external {
    //     _checkKeyAndMsgSender(user, _indexToken, _isLong);
    //     orderBook.updateDecreaseOrder(
    //         _orderIndex,
    //         _collateralDelta,
    //         _sizeDelta,
    //         _triggerPrice,
    //         _triggerAboveThreshold
    //     );
    // }

    /**
     * @param value maxUnit256
     **/
    function approveAll(uint256 value) public {
        router.approvePlugin(address(positionRouter));
        for (uint256 i = 0; i < 4; i++) {
            ERC20(indexTokenArray[i]).approve(address(positionRouter), value);
            ERC20(indexTokenArray[i]).approve(address(router), value);
        }
        USDC.approve(address(positionRouter), value);
        USDC.approve(address(router), value);
    }
}

