contracts/DDL_GMX.sol.content:
pragma solidity 0.8.6||0.7.0;

import "./DDL.sol";
import "./interfaces/IAccountManager.sol";
import "./interfaces/IVault.sol";
import "./interfaces/IPositionRouter.sol";
import "./Doppelganger.sol";
import "hardhat/console.sol";

contract DDL_GMX is DDL {
    IAccountManager public accountManager;
    IVault public vault;
    IPositionRouter public positionRouter;
    int256 public closeSlippage = 10;
    uint256 internal constant GMX_DECIMALS = 1e30;

    struct PositionInfo {
        address indexToken;
        uint256 amount;
        uint256 openPrice;
        bool isLong;
    }

    mapping(uint256 => PositionInfo) public positionInfo;

    constructor(
        IPositionRouter _positionRouter,
        IAccountManager _accountManager,
        IVault _vault,
        IERC721 _collateralToken,
        IERC20 _USDC,
        uint256 _minBorrowLimit,
        uint256 _ltv,
        uint256 _COLLATERAL_DECIMALS,
        uint256 _PriorLiqPriceCoef
    )
        DDL(
            _collateralToken,
            _USDC,
            _minBorrowLimit,
            _ltv,
            _COLLATERAL_DECIMALS,
            _PriorLiqPriceCoef
        )
    {
        positionRouter = _positionRouter;
        accountManager = _accountManager;
        vault = _vault;
    }

    /**
     * @notice takes ERC-721 (collateral) from the user and locking in DDL_GMX
     * @param id collateral ID
     * @param user user's address
     **/
    function _lockCollateral(uint256 id, address user) internal override {
        (string memory symbol, , bool isLong, , ) = accountManager.keyData(id);
        address indexToken = accountManager.indexTokenBySymbol(symbol);
        (uint256 size, , uint256 averagePrice, , , , ) = accountManager
            .getPosition(id);
        collateralToken.transferFrom(msg.sender, address(this), id);
        positionInfo[id] = PositionInfo(indexToken, size, averagePrice, isLong);
    }

    /**
     * @notice check the ability to get borrow USDC by collateral ID
     * @param id collateral ID
     **/
    function _isAvaialbleToBorrow(uint256 id) internal override {
        (bool isProfit, ) = accountManager.getPositionDelta(id);
        require(isProfit, "no profit");
    }

    function _emitBorrow(
        address user,
        uint256 id,
        uint256 amount,
        uint256 timestamp
    ) internal override {
        emit Borrow(
            user,
            id,
            amount,
            positionInfo[id].indexToken,
            block.timestamp
        );
    }

    /**
     * @notice calculates the intrinsic value of the collateral
     * @param id collateral ID
     **/
    function intrinsicValueOf(uint256 id)
        public
        view
        override
        returns (uint256 delta)
    {
        (bool isProfit, uint256 profit) = accountManager.getPositionDelta(id);
        profit = profit / (GMX_DECIMALS / 1e6);
        return (isProfit ? profit : 0);
    }

    /**
     * @notice closes position on GMX
     * @param id collateral id
     **/
    function _liquidateCollateral(uint256 id) internal {
        PositionInfo memory data = positionInfo[id];
        (, address doppelgangerContract, , , ) = accountManager.keyData(id);
        (uint256 size, , , , , , ) = accountManager.getPosition(id);
        require(size != 0, "position size is null");
        uint256 closePrice = (accountManager.currentPrice(id) / 1000) *
            uint256(
                data.isLong
                    ? int256(1000) - closeSlippage
                    : int256(1000) + closeSlippage
            );
        (
            address[] memory path,
            uint256 minOut,
            uint256 closeValue
        ) = _preparationData(id);
        uint256 executionFee = positionRouter.minExecutionFee();
        console.log("intrinsicValueOf",intrinsicValueOf(id));
        Doppelganger(doppelgangerContract).createDecreasePosition(
            address(this),
            path,
            data.indexToken,
            closeValue,
            size,
            data.isLong,
            closePrice,
            minOut,
            executionFee,
            false
        );
    }

     /**
     * @notice used to liquidate the loan
     * @param id position ID
     **/
    function liquidate(uint256 id) external payable {
        require(collateralState(id), "invalid price");
        BorrowedByCollateral storage data = borrowedByCollateral[id];
        uint256 profit = intrinsicValueOf(id);
        uint256 executionFee = positionRouter.minExecutionFee();
        (, address doppelgangerContract, , , ) = accountManager.keyData(id);
        Doppelganger(doppelgangerContract).deposit{
            value: executionFee
        }();
        console.log("BBBB",USDC.balanceOf(address(this)));
        _liquidateCollateral(id);
        console.log("AAAAAAA",USDC.balanceOf(address(this)));
        uint256 diff = 0;
        pool.subTotalLocked(data.borrowed);
        if (profit > data.borrowed) {
            diff = profit - data.borrowed;
            USDC.transfer(address(pool), data.borrowed + (diff * 90) / 100);
            USDC.transfer(msg.sender, (diff * 10) / 100);
        } else {
            USDC.transfer(address(pool), profit);
        }
        emit Liquidate(
            collateralOwner[id],
            id,
            data.borrowed,
            (diff * 90) / 100,
            (diff * 10) / 100
        );
    }

    /**
     * @notice used to liquidate the loan by border price
     * @param id collateral ID
     **/
     function liquidateByBorderPrice(uint256 id) external payable {
        require(collateralStateByBorderPrice(id), "invalid price");
        BorrowedByCollateral storage data = borrowedByCollateral[id];
        uint256 profit = intrinsicValueOf(id);
        uint256 executionFee = positionRouter.minExecutionFee();
        (, address doppelgangerContract, , , ) = accountManager.keyData(id);
        Doppelganger(doppelgangerContract).deposit{
            value: executionFee
        }();
        _liquidateCollateral(id);
        pool.subTotalLocked(data.borrowed);
        console.log(USDC.balanceOf(address(this)), data.borrowed);
        USDC.transfer(address(pool), data.borrowed);
        USDC.transfer(
            collateralOwner[id],
            profit - (data.borrowed + (data.borrowed * 10) / 100)
        );
        USDC.transfer(msg.sender, (data.borrowed * 10) / 100);
        emit LiquidateByBorderPrice(
            collateralOwner[id],
            id,
            profit - (data.borrowed + (data.borrowed * 10) / 100),
            data.borrowed,
            (data.borrowed * 10) / 100
        );
    }

    function _preparationData(uint256 id)
        internal
        returns (
            address[] memory path,
            uint256 minOut,
            uint256 closeValue
        )
    {
        (, uint256 collateral, , , , , ) = accountManager.getPosition(id);
        (bool isProfit, uint256 delta) = accountManager.getPositionDelta(id);
        closeValue = (isProfit ? collateral : delta);
        if (positionInfo[id].isLong) {
            path = new address[](2);
            path[0] = positionInfo[id].indexToken;
            path[1] = address(USDC);
            minOut = (closeValue * 99) / 100;
        } else {
            path = new address[](1);
            path[0] = address(USDC);
            minOut = 0;
        }
    }

    function isLong(uint256 id) public view override returns (bool) {
        PositionInfo memory collateral = positionInfo[id];
        return collateral.isLong;
    }

    function currentPrice(uint256 id) public view override returns (uint256) {
        PositionInfo memory data = positionInfo[id];
        if (isLong(id)) {
            return vault.getMaxPrice(data.indexToken)/(GMX_DECIMALS/COLLATERAL_DECIMALS);
        }
        return vault.getMinPrice(data.indexToken)/(GMX_DECIMALS/COLLATERAL_DECIMALS);
    }

    /**
     * @notice returns the position size and the entry price by collateral ID
     * @param id collateral id
     **/
    function collateralInfo(uint256 id)
        public
        view
        override
        returns (uint256 amount, uint256 price)
    {
        (uint256 size, , uint256 averagePrice, , , , ) = accountManager
            .getPosition(id);
        return (size/(GMX_DECIMALS/COLLATERAL_DECIMALS), averagePrice/(GMX_DECIMALS/COLLATERAL_DECIMALS));
    }
}

contracts/DDL.sol.content:
pragma solidity 0.8.6;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./interfaces/IPoolDDL.sol";
import "hardhat/console.sol";

abstract contract DDL is Ownable {
    using SafeERC20 for IERC20;

    uint256 public LTV;
    uint256 public LTV_DECIMALS = 10**4;

    IERC721 public collateralToken;
    IERC20 public USDC;

    uint256 public interestRate = 19025875190258754083880960;
    uint256 public INTEREST_RATE_DECIMALS = 10**30;

    uint256 public minBorrowLimit;
    uint256 public COLLATERAL_DECIMALS;

    uint256 public BorderPriceCoef;

    IPoolDDL public pool;

    struct BorrowedByCollateral {
        uint256 borrowed;
        uint256 newBorrowTimestamp;
    }

    mapping(uint256 => BorrowedByCollateral) public borrowedByCollateral;
    mapping(uint256 => address) public collateralOwner;

    event Borrow(
        address indexed user,
        uint256 indexed id,
        uint256 amount,
        address strategy,
        uint256 timestamp
    );
    // discuss about strategy

    event Repay(address indexed user, uint256 indexed optionID, uint256 amount);
    event Liquidate(
        address indexed user,
        uint256 indexed optionID,
        uint256 amount,
        uint256 poolProfit,
        uint256 liqFee
    );
    event Unlock(address indexed user, uint256 indexed optionID);
    event ForcedExercise(
        address indexed user,
        uint256 indexed optionID,
        uint256 amount,
        uint256 poolProfit,
        uint256 liqFee
    );
    event LiquidateByBorderPrice(
        address indexed user,
        uint256 indexed optionID,
        uint256 userReturn,
        uint256 poolReturn,
        uint256 liqFee
    );

    constructor(
        IERC721 _collateralToken,
        IERC20 _USDC,
        uint256 _minBorrowLimit,
        uint256 _ltv,
        uint256 _COLLATERAL_DECIMALS,
        uint256 _BorderPriceCoef
    ) {
        collateralToken = _collateralToken;
        USDC = _USDC;
        minBorrowLimit = _minBorrowLimit;
        LTV = _ltv;
        COLLATERAL_DECIMALS = 10**_COLLATERAL_DECIMALS;
        BorderPriceCoef = _BorderPriceCoef;
    }

    /**
     * @notice used to set LTV coefficient
     * @param value LTV coefficient.
     **/
    function setLTV(uint256 value) external onlyOwner {
        require(value <= 8000, "invalid value");
        LTV = value;
    }

    /**
     * @notice used to set new interest rate
     * @param value interest rate (in minutes)
     **/
    function setInterestRate(uint256 value) external onlyOwner {
        interestRate = value;
    }

    function setInterestRateDecimals(uint256 value) external onlyOwner {
        INTEREST_RATE_DECIMALS = value;
    }

    /**
     * @notice used to set the minimum borrow size
     * @param value min. borrow size (USDC)
     **/
    function setMinBorrowLimit(uint256 value) external onlyOwner {
        minBorrowLimit = value;
    }

    /**
     * @notice used to connect a new liqidity pool
     * @param value the address of the liquidity pool
     **/
    function setPool(address value) external onlyOwner {
        pool = IPoolDDL(value);
    }

    /**
     * @notice used to lock the collateral (ERC-721) in DeDeLend
     * @param id collateral ID
     **/
    function lockCollateral(uint256 id) external {
        require(pool.openDeDeLend(), "pauseDeDeLend");
        require(collateralToken.ownerOf(id) == msg.sender, "you not owner");
        collateralOwner[id] = msg.sender;
        _lockCollateral(id, msg.sender);
    }

    function _lockCollateral(uint256 id, address user) internal virtual;

    /**
     * @notice calcualtes the maximum borrow size
     * for the specific collateral
     * @param id collateral ID
     **/
    function maxBorrowLimit(uint256 id) public view returns (uint256) {
        return (intrinsicValueOf(id) / LTV_DECIMALS) * LTV;
    }

    /**
     * @notice send USDC to the user
     * @param id collateral ID
     * @param amount borrow size (USDC)
     **/
    function borrow(uint256 id, uint256 amount) external {
        require(pool.openDeDeLend(), "pauseDeDeLend");
        BorrowedByCollateral storage data = borrowedByCollateral[id];
        uint256 maxLimit = maxBorrowLimit(id);
        uint256 totalBalance = pool.getTotalBalance();
        require(amount >= minBorrowLimit, "amount less minBorrowLimit");
        require(amount + data.borrowed <= maxLimit, "borrow is too big");
        require(msg.sender == collateralOwner[id], "you are not the owner");
        require(
            amount <= totalBalance,
            "there is not enough money in the pool"
        );
        _isAvaialbleToBorrow(id);
        if (isLong(id)) {
            require(currentPrice(id) > borderPrice(id), "the price is too low");
        } else {
            require(
                currentPrice(id) < borderPrice(id),
                "the price is too high"
            );
        }
        uint256 upcomingFee = calculateUpcomingFee(id);
        borrowedByCollateral[id] = BorrowedByCollateral(
            amount + data.borrowed + upcomingFee,
            block.timestamp
        );
        pool.addTotalLocked(amount + upcomingFee);
        pool.send(collateralOwner[id], amount);
        _emitBorrow(msg.sender, id, amount, block.timestamp);
    }

    function _isAvaialbleToBorrow(uint256 id) internal virtual;

    function _emitBorrow(
        address user,
        uint256 id,
        uint256 amount,
        uint256 timestamp
    ) internal virtual;

    // /**
    //  * @notice used to liquidate the loan
    //  * @param id position ID
    //  **/
    // function liquidate(uint256 id) external {
    //     require(collateralState(id), "invalid price");
    //     BorrowedByCollateral storage data = borrowedByCollateral[id];
    //     uint256 profit = intrinsicValueOf(id);
    //     _liquidateCollateral(id);
    //     uint256 diff = 0;
    //     pool.subTotalLocked(data.borrowed);
    //     if (profit > data.borrowed) {
    //         diff = profit - data.borrowed;
    //         USDC.transfer(address(pool), data.borrowed + (diff * 90) / 100);
    //         USDC.transfer(msg.sender, (diff * 10) / 100);
    //     } else {
    //         USDC.transfer(address(pool), profit);
    //     }
    //     emit Liquidate(
    //         collateralOwner[id],
    //         id,
    //         data.borrowed,
    //         (diff * 90) / 100,
    //         (diff * 10) / 100
    //     );
    // }

    // function _liquidateCollateral(uint256 id) internal virtual;


    // /**
    //  * @notice used to liquidate the loan by border price
    //  * @param id collateral ID
    //  **/
    // function liquidateByBorderPrice(uint256 id) external {
    //     require(collateralStateByBorderPrice(id), "invalid price");
    //     BorrowedByCollateral storage data = borrowedByCollateral[id];
    //     uint256 profit = intrinsicValueOf(id);
    //     _liquidateCollateral(id);
    //     pool.subTotalLocked(data.borrowed);
    //     USDC.transfer(address(pool), data.borrowed);
    //     USDC.transfer(
    //         collateralOwner[id],
    //         profit - (data.borrowed + (data.borrowed * 10) / 100)
    //     );
    //     USDC.transfer(msg.sender, (data.borrowed * 10) / 100);
    //     emit LiquidateByBorderPrice(
    //         collateralOwner[id],
    //         id,
    //         profit - (data.borrowed + (data.borrowed * 10) / 100),
    //         data.borrowed,
    //         (data.borrowed * 10) / 100
    //     );
    // }

    /**
     * @notice used to calculate how much USDC
     * user should pay as interest fee
     * @param id collateral ID
     **/
    function calculateUpcomingFee(uint256 id)
        public
        view
        returns (uint256 upcomingFee)
    {
        BorrowedByCollateral storage data = borrowedByCollateral[id];
        uint256 periodInMinutes = (block.timestamp - data.newBorrowTimestamp) /
            60;
        upcomingFee =
            ((data.borrowed / 100) * (periodInMinutes * interestRate)) /
            INTEREST_RATE_DECIMALS;
    }

    /**
     * @notice used to repay the user's debt
     * @param id collateral ID
     * @param amount amount to repay (USDC)
     **/
    function repay(uint256 id, uint256 amount) external {
        require(borrowedByCollateral[id].borrowed > 0, "option redeemed");
        uint256 upcomingFee = calculateUpcomingFee(id);
        require(
            amount <= borrowedByCollateral[id].borrowed + upcomingFee,
            "too much amount"
        );
        require(msg.sender == collateralOwner[id]);
        uint256 newBorrow = borrowedByCollateral[id].borrowed +
            upcomingFee -
            amount;
        pool.subTotalLocked(amount - upcomingFee);
        borrowedByCollateral[id] = BorrowedByCollateral(
            newBorrow,
            block.timestamp
        );
        USDC.transferFrom(collateralOwner[id], address(this), amount);
        USDC.transfer(address(pool), amount);
        emit Repay(msg.sender, id, amount);
    }

    /**
     * @notice sends the collateral token back to the user
     * @param id collateral ID
     **/
    function unlock(uint256 id) external {
        require(borrowedByCollateral[id].borrowed == 0, "loan is blocked");
        collateralToken.transferFrom(address(this), collateralOwner[id], id);
        emit Unlock(msg.sender, id);
    }

    function isLong(uint256 id) public view virtual returns (bool);

    function collateralState(uint256 id) public view returns (bool) {
        if (isLong(id)) {
            return currentPrice(id) <= liqPrice(id);
        } else {
            return currentPrice(id) >= liqPrice(id);
        }
    }

    function collateralStateByBorderPrice(uint256 id)
        public
        view
        returns (bool)
    {
        if (isLong(id)) {
            return currentPrice(id) <= borderPrice(id);
        } else {
            return currentPrice(id) >= borderPrice(id);
        }
    }

    function currentPrice(uint256 id)
        public
        view
        virtual
        returns (uint256 price)
    {}

    /**
     * @param id positon ID
     * @notice returns position size and entryPrice
     **/
    function collateralInfo(uint256 id)
        public
        view
        virtual
        returns (uint256 amount, uint256 price);

    function borderPrice(uint256 id) public view returns (uint256 price) {
        (, uint256 openPrice) = collateralInfo(id);
        if (isLong(id)) {
            return (openPrice * (100 + BorderPriceCoef)) / 100;
        }
        return (openPrice * (100 - BorderPriceCoef)) / 100;
    }

    function liqPrice(uint256 id) public view returns (uint256 price) {
        BorrowedByCollateral storage data = borrowedByCollateral[id];
        (uint256 amount, uint256 openPrice) = collateralInfo(id);
        console.log("liqPrice", data.borrowed, openPrice, (data.borrowed * COLLATERAL_DECIMALS));
        console.log(amount, (data.borrowed * COLLATERAL_DECIMALS)/amount);
        if (isLong(id)) {
            return
                openPrice +
                ((data.borrowed * COLLATERAL_DECIMALS) / (amount)) *
                120;
        }
        return
            openPrice -
            ((data.borrowed * COLLATERAL_DECIMALS) / (amount)) *
            120;
    }

    function currentBorderPrice(uint256 id)
        public
        view
        returns (uint256 price)
    {
        if (isLong(id)) {
            if (borderPrice(id) > liqPrice(id)) {
                return borderPrice(id);
            }
            return liqPrice(id);
        }
        if (borderPrice(id) < liqPrice(id)) {
            return borderPrice(id);
        }
        return liqPrice(id);
    }

    /**
     * @notice position's value
     * @param id position ID
     **/
    function intrinsicValueOf(uint256 id)
        public
        view
        virtual
        returns (uint256 profit);
}

