pragma solidity 0.8.6||0.7.0;

import "./DDL.sol";
import "./interfaces/IHegicOperationalTreasury.sol";
import "./interfaces/IHegicStrategy.sol";

contract DDL_Hegic is Ownable, DDL {
    IHegicOperationalTreasury public operationalPool;
    struct OptionInfo {
        address strategyAddress;
        uint256 amount;
        uint256 strike;
        uint256 expiration;
        bool isLong;
    }
    enum HegicStrategyType {
        Invalid,
        Long,
        Short
    }
    mapping(address => HegicStrategyType) public strategyType;
    mapping(uint256 => OptionInfo) public optionInfo;

    constructor(
        address[4] memory _arrLongHegicStrategy,
        address[4] memory _arrShortHegicStrategy,
        IERC721 _collateralToken,
        IHegicOperationalTreasury _operationalPool,
        IERC20 _USDC,
        uint256 _minBorrowLimit,
        uint256 _ltv,
        uint256 _COLLATERAL_DECIMALS,
        uint256 _PriorLiqPriceCoef
    )
        DDL(
            _collateralToken,
            _USDC,
            _minBorrowLimit,
            _ltv,
            _COLLATERAL_DECIMALS,
            _PriorLiqPriceCoef
        )
    {
        for (uint256 i = 0; i < _arrLongHegicStrategy.length; i++) {
            strategyType[_arrLongHegicStrategy[i]] = HegicStrategyType.Long;
        }
        for (uint256 i = 0; i < _arrShortHegicStrategy.length; i++) {
            strategyType[_arrShortHegicStrategy[i]] = HegicStrategyType.Short;
        }
        operationalPool = _operationalPool;
    }

    function _lockCollateral(uint256 id, address user) internal override {
        (
            IHegicOperationalTreasury.LockedLiquidityState state,
            address strategy,
            ,
            ,
            uint32 expiration
        ) = operationalPool.lockedLiquidity(id);
        require(block.timestamp <= uint256(expiration) - 60 * 60, "too late");
        (uint128 amount, uint128 strike) = IHegicStrategy(strategy)
            .strategyData(id);
        require(
            state == IHegicOperationalTreasury.LockedLiquidityState.Locked,
            "option is active"
        );
        require(
            strategyType[strategy] != HegicStrategyType.Invalid,
            "strategy not supported"
        );
        collateralToken.transferFrom(msg.sender, address(this), id);
        bool isLong = strategyType[strategy] == HegicStrategyType.Long
            ? true
            : false;
        optionInfo[id] = OptionInfo(
            strategy,
            uint256(amount),
            uint256(strike),
            uint256(expiration),
            isLong
        );
    }

    function _isAvaialbleToBorrow(uint256 id) internal override {
        (
            IHegicOperationalTreasury.LockedLiquidityState state,
            ,
            ,
            ,

        ) = operationalPool.lockedLiquidity(id);
        require(
            state == IHegicOperationalTreasury.LockedLiquidityState.Locked,
            "invalid state"
        );
        require(
            block.timestamp <= optionInfo[id].expiration - 60 * 60,
            "too late"
        );
    }

    function _emitBorrow(
        address user,
        uint256 id,
        uint256 amount,
        uint256 timestamp
    ) internal override {
        emit Borrow(
            user,
            id,
            amount,
            optionInfo[id].strategyAddress,
            block.timestamp
        );
    }

    function intrinsicValueOf(uint256 id)
        public
        view
        override
        returns (uint256 delta)
    {
        (, address strategy, , , ) = operationalPool.lockedLiquidity(id);
        return IHegicStrategy(strategy).profitOf(id);
    }

    // function _liquidateCollateral(uint256 id) internal override {
    //     OptionInfo storage data = optionInfo[id];
    //     return IHegicStrategy(data.strategyAddress).exercise(id);
    // }

    // function forcedExercise(uint256 id) external {
    //     require(block.timestamp > optionInfo[id].expiration - 30 * 60);
    //     BorrowedByCollateral storage data = borrowedByCollateral[id];
    //     uint256 profit = intrinsicValueOf(id);
    //     _liquidateCollateral(id);
    //     uint256 diff = 0;
    //     pool.subTotalLocked(data.borrowed);
    //     if (profit > data.borrowed) {
    //         diff = profit - data.borrowed;
    //         USDC.transfer(address(pool), data.borrowed + (diff * 90) / 100);
    //         USDC.transfer(msg.sender, (diff * 10) / 100);
    //     } else {
    //         USDC.transfer(address(pool), profit);
    //     }
    //     emit ForcedExercise(
    //         collateralOwner[id],
    //         id,
    //         data.borrowed,
    //         (diff * 90) / 100,
    //         (diff * 10) / 100
    //     );
    // }

    function isLong(uint256 id) public view override returns (bool) {
        OptionInfo memory collateral = optionInfo[id];
        return collateral.isLong;
    }

    function currentPrice(uint256 id)
        public
        view
        override
        returns (uint256 price)
    {
        OptionInfo storage data = optionInfo[id];
        (, int256 latestPrice, , , ) = IHegicStrategy(data.strategyAddress)
            .priceProvider()
            .latestRoundData();
        price = uint256(latestPrice);
    }

    function collateralInfo(uint256 id)
        public
        view
        override
        returns (uint256 amount, uint256 price)
    {
        OptionInfo storage data = optionInfo[id];
        (, address strategy, , , ) = operationalPool.lockedLiquidity(id);
        (uint128 optionAmount, uint128 optionStrike) = IHegicStrategy(strategy)
            .strategyData(id);
        return (uint256(optionAmount), uint256(optionStrike));
    }
}

